"use strict";

(function (window) {
  let App = window.App || {};
  class DataStore {
    constructor() {
      this.data = {};
    }
    add(key, value) {
      this.data[key] = value;
    }
    get(key) {
      return this.data[key];
    }
    getAll() {
      return this.data;
    }
    remove(key) {
      delete this.data[key];
    }
  }

  App.DataStore = DataStore;
  window.App = App;
})(window);

(function (window) {
  "use strict";
  let App = window.App || {};

  class Truck {
    constructor(truckId, db) {
      this.truckId = truckId;
      this.db = db;
    }

    createOrder(order) {
      this.db.add(order.emailAddress, order);
    }

    deliverOrder(customerId) {
      this.db.remove(customerId);
    }

    printOrders() {
      let customerIdArray = Object.keys(this.db.getAll());
      console.log("Truck #" + this.truckId + " has pending orders:");
      customerIdArray.forEach(function (id) {
        console.log(this.db.get(id));
      }, this);
    }
  }

  App.Truck = Truck;
  window.App = App;
})(window);

(function (window) {
  "use strict";
  let App = window.App;
  let Truck = App.Truck;
  let DataStore = App.DataStore;
  let myTruck = new Truck("ncc-1701", new DataStore());
  window.myTruck = myTruck;
  window.App = App;
})(window);
